//页面初始化调用
$(function() {
	var page = $("#curPage").val();
	if (page == "add") {
		createNo();
	}

	$("[name='FIRST_TYPE']").change(getSelectedOption); // 获得选中的元素
	$("[name='IS_MAIN_DEVICE']").click(disableText);
	var optionValue = '';
	if (pObj == null) {
		optionValue = '9200009561115';
	} else {
		optionValue = pObj.FIRST_TYPE;
	}


	/**生成各种按钮*/
	var $btn_unlock = createBtn("解锁",unComfirmData,"btn_unlock");
	$("#retu").before($btn_unlock);
	var $btn_lock = createBtn("提交",comfirmData,"btn_lock");
	$("#btn_update").after($btn_lock);
	var $btn_createQR = createBtn1("生成二维码",printQrImage,"btn_createQR");
	$("#retu").before($btn_createQR);

	if (!commonAuthor.hasAuthoritySignByFunction("sign_submit")) {// 提交权限
		$btn_lock.hide();
	}
	if (!commonAuthor.hasAuthoritySignByFunction("sign_unlock")) {// 解锁权限
		$btn_unlock.hide();
	}
	if (!commonAuthor.hasAuthoritySignByFunction("sign_createqr")) {// 生成二维码权限
		$btn_createQR.hide();
	}
	var isComfirm = $("[name='LOCK_STATE']").val();
	if (isComfirm == "1110872") {//上锁
		$("#btn_update").hide();
		$("#btn_delete").hide();
		$("#btn_print").hide();
		$btn_lock.hide();
	} else {//未锁定
		$("#btn_export").hide();
		$btn_unlock.hide();
	}
	// alert(optionValue);
	showOption(optionValue);
	disableText();
	//$("#standingSaveBtn").attr("onclick", "").click(saveData);


	if (page == "add") {
		onChangeAssetsType();
		$("select[name='ASSETS_TYPE']").change(onChangeAssetsType);
	}



});

function comfirmData() {
	// var currentid= pmGetRequestPara("currentid");
	var url = basePath + "/asset/assetsMainInfo.action?method=isComfirmInfo&currentid=" + currentid;
	$.ajax({
		type : "post",
		url : url,
		// data: datas,
		success : function() {
			new iMsgBox("信息已确认!", "系统提示").showAlert();
			dynOpenLocation(window.location.href);
		},
		error : function(XMLHttpRequest, textStatus) {
			_error = new Array();
			_error[0] = "操作异常！" + textStatus;
			errorMsgWindow();
			return false;
		}
	});
}

function unComfirmData() {
	// var currentid= pmGetRequestPara("currentid");
	var url = basePath + "/asset/assetsMainInfo.action?method=unIsComfirmInfo&currentid=" + currentid;
	$.ajax({
		type : "post",
		url : url,
		// data: datas,
		success : function() {
			// window.location.href=window.location.href+"&comfirmFlag=1120303";
			// alert(url);
			new iMsgBox("信息已解锁!", "系统提示").showAlert();
			dynOpenLocation(window.location.href);
		},
		error : function(XMLHttpRequest, textStatus) {
			_error = new Array();
			_error[0] = "操作异常！" + textStatus;
			errorMsgWindow();
			return false;
		}
	});
}


function getSelectedOption() {
	/* 逐个弹出每个元素 */
	var options = $("[name='FIRST_TYPE']> option:selected");
	/* 逐个弹出每个元素，第一种的简写 */
	$("[name='FIRST_TYPE'] > option:selected").each(function() {
		// alert('option2: ' + this.value);
		showOption(this.value);
	});
} 

