function disableText(){
    var checkedValue= $("input[type=radio][name='IS_MAIN_DEVICE'][@checked]").val();
    if(checkedValue=="1110872"){
         $("#name_P_ID").val("");
         $("#P_ID").val("");
         $("#name_P_ID").attr("disabled","disabled");
         $(":button[name='P_ID']").attr("disabled","disabled");
    }else{
         $("#name_P_ID").removeAttr("disabled");
         $(":button[name='P_ID']").removeAttr("disabled");
    }
}
//选择供货商
function getShop(){
    var showCols='name,shopNo,orgNo,linkman'; //展示项
    var conCols ='name,shopNo';//查询项
    var sql = " delete_flag =0";//SQL语句,默认在where加再之后
    utilList('T_SCMM_SHOP',showCols,conCols,sql,'0','','showShop');//0单选,1多选
}
function showShop(list){
     if(list.length==1){
        var obj = list[0]; 
        $("input[name='name_SUPPLIER']").val(obj.name);
        $("input[name='FACTORY']").val(obj.name);
        $("#SUPPLIER").val(obj.id);
        }else{
        new iMsgBox("请选择一个供货商!", "错误提示").showAlert();
    }
}

function selParent(){
   var showCols='assetsNo,erpDeviceNo,name,useMan'; //展示项
    var conCols ='assetsNo,name';//查询项
    var sql = " delete_flag =0 and IS_MAIN_DEVICE='1110872'";//SQL语句,默认在where加再之后
    if(currentid.value!=""){
       sql+=" and id!='"+currentid.value+"'";
    }
    utilList('T_SCMM_ASSETS_MAININFO',showCols,conCols,sql,'0','','setData');//0单选,1多选
}

function setData(list){
    if(list.length==1){
        var obj = list[0]; 
        $("input[name='name_P_ID']").val(obj.assetsNo);
        $("#P_ID").val(obj.id);
        }else{
        new iMsgBox("请选择一个主设备!", "错误提示").showAlert();
    }
}

function saveStanding(){
	var win = getRootWindow();
	var test = new win.DivWin.DivWin("confirm", "250px",  "");
	test.clickYes = function (){
		$("[name='LOCK_STATE']").val("1110872");//是
		win.closeDisabledWindow();
		test.close();
		saveData();
	}
	test.clickNo = function(){
		$("[name='LOCK_STATE']").val("1110873");//否
		win.closeDisabledWindow();
		test.close();
		saveData();
	}
	test.confirm("是否需要提交锁定？","是","否");
}

//保存台账调用此方法
function saveData() {
	var currentid = pmGetRequestPara("currentid");
	var assetsNo = $("[name='ASSETS_NO']").val();
	var erpDeviceNo = $("[name='ERP_DEVICE_NO']").val();
	var erpAssNo = $("[name='ERP_ASS_NO']").val();
	var checkedValue = $("input[type=radio][name='IS_MAIN_DEVICE'][@checked]").val();
	if (checkedValue == '1110872') {
		$("#P_ID").val("-1");
	}

	// 验证项目编号是否重复
	$.ajax({
		type : "get",
		url : basePath
				+ "/asset/assetsMainInfo.action?method=isRepeatNo&assetsNo="
				+ assetsNo + "&erpDeviceNo=" + erpDeviceNo + "&erpAssNo="
				+ erpAssNo + "&currentid=" + currentid,
		async : false,
		dataType : 'json',
		success : function(data) {
			// alert(currentid+"###"+cardNo+"###"+data);
			if (data == false) {
				new iMsgBox(
						"请检查【资产编号】，【ERP设备编号】，【ERP资产编号】输入项，不能和系统中已有资产信息重复！",
						"填写提示", "e").showAlert();
				// alert("编号重复");
			} else {
				saveStanding_target();
			}
		}
	});

}
function createNo() {
	$.ajax({
		url : basePath + "CodeAction!createZCGLCode.action?type=ZC",
		dataType : "json",
		success : function(json) {
			$("input[name='ASSETS_NO']").val(json.code);
		}
	});
}

function printQrImage(){
     // var currentid= pmGetRequestPara("currentid");
     var url=basePath+"/asset/assetsMainInfo.action?method=displayOrCreateQR&currentid="+currentid;
     //var url=basePath+"scmmnew/zcgl/jsp/displayQrCode.jsp?currentid="+currentid;
     //window.open(url);
     openDynWin3(url);
    // alert(currentid);
}

function onChangeAssetsType (){
	var assetsType = updateForm.ASSETS_TYPE.value;//资产类型
	if (assetsType == "9200009561141") {//非ERP资产
		$.ajax({
			url : basePath + "CodeAction!createZCGLCode.action?type=F1900",
			dataType : "json",
			success : function(json) {
				$("input[name='ERP_DEVICE_NO']").val(json.code);
			}
		});
		$.ajax({
			url : basePath + "CodeAction!createZCGLCode.action?type=F",
			dataType : "json",
			success : function(json) {
				$("input[name='ERP_ASS_NO']").val(json.code);
			}
		});
	}else{
		$("input[name='ERP_DEVICE_NO']").val("");
		$("input[name='ERP_ASS_NO']").val("");
	}
}

