//初始化调用
$(function(){
	var url =basePath+"asset/assetsMainInfo.action?method=getMainInfo";
	$("#btn_export").removeAttr("onclick");
	$("#btn_export").click(
			function() {
				var validatePwdUrl = basePath
						+ "/getUser.action?method=validatePasswordBefore&type=1";
				showDiv(validatePwdUrl, "validatePwdWindow", "400", "200", "export1");
				});
	   $.ajax({
			type: "post",
		url: url,
		//data: datas,
		success: function(info){    
	                    //$("#info").text(data);
	                    var infoHtml = "<tr><td colspan='3' style='text-align:left;color: red;'>"+info+"资产净值为红色的表示需要报废！</td></tr>";
	                    $("#dataTable").prepend(infoHtml);
	                    resizeTable();
	    		},
		error:function(XMLHttpRequest, textStatus){
			_error = new Array();
			_error[0] = "操作异常！"+textStatus;
			errorMsgWindow();
			return false;
		}
	   });
	   var $btn_createQR = createBtn1("生成二维码",viewQrCode,"btn_createQR");
	   var $btn_import = createBtn("导入",validatePwd_import,"btn_import");
	   $("#btn_export").after($btn_createQR);
	   $("#btn_export").after($btn_import);
	   if (!commonAuthor.hasAuthoritySignByFunction("sign_import")) {// 导入权限
		   $btn_import.hide();
	   }
	   if (!commonAuthor.hasAuthoritySignByFunction("sign_createqr")) {// 生成二维码权限
			$btn_createQR.hide();
	   }
	   resizeQueryDiv();

});



function validatePwd_import(){
	var validatePwdUrl = basePath
			+ "/getUser.action?method=validatePasswordBefore&type=2";
	showDiv(validatePwdUrl, "validatePwdWindow", "400", "200",
			"importFunction");

}
function importFunction(){
    var url=basePath+"scmmnew/zcgl/jsp/zcglMainInfoImport.jsp";
    var testWin = new iMsgBox("bg","资产主台账");
    testWin.showDialog(appendQuery(url), '400', '200',"popwinImport");
    getRootWindow().DivWin.callBack=function(){
       // window.location.href = window.location.href;
        dynOpenLocation(window.location.href);
    }
 }

//列表数据加载后执行的业务方法
function afterListDataLoad1() {
  //mygrid.insertColumn(1,"Some text","ch",120,"na","center","center",null,"red");
   mygrid.insertColumn(0,"<input type=checkbox onclick=checkAll(this)>","ch");
   mygrid.setColumnMinWidth("100","29");

	//购置价值为0的且不是报废的，提醒报废
	var orgiValueIndex = 19;//mygrid.getColIndexById("ORIG_VALUE@@@0");
	var assetsStateIndex = 17;//mygrid.getColIndexById("ITEM_NAME_ASSETS_STATE_DESC@@@0");
	var rownum = mygrid.getRowsNum();
	for (i = 0; i < rownum; i++) {
		var orgiValueCell = mygrid.cells2(i, orgiValueIndex);
		var assetsStateValue = mygrid.cells2(i, assetsStateIndex).getValue();
		var str = orgiValueCell.getValue();
		//alert(i+"="+str);
		//alert(i+"=="+assetsStateValue );
		var num = parseFloat(str);
		if (!isNaN(num) && num == 0 && assetsStateValue != "报废") {
			var tempStr = "<font color='red'>" + str + "</font>";
			orgiValueCell.setValue(tempStr);
		}
	}
}
