function afterListDataLoad() {
  //mygrid.insertColumn(1,"Some text","ch",120,"na","center","center",null,"red");
   mygrid.insertColumn(0,"<input type=checkbox onclick=checkAll(this)>","ch");
   mygrid.setColumnMinWidth("100","29");

	//购置价值为0的且不是报废的，提醒报废
	var orgiValueIndex = 19;//mygrid.getColIndexById("ORIG_VALUE@@@0");
	var assetsStateIndex = 17;//mygrid.getColIndexById("ITEM_NAME_ASSETS_STATE_DESC@@@0");
	var rownum = mygrid.getRowsNum();
	for (i = 0; i < rownum; i++) {
		var orgiValueCell = mygrid.cells2(i, orgiValueIndex);
                  var assetsStateCell = mygrid.cells2(i, assetsStateIndex);

		var assetsStateValue = mygrid.cells2(i, assetsStateIndex).getValue();
		var str = orgiValueCell.getValue();
		//alert(i+"="+str);
		//alert(i+"=="+assetsStateValue );
		var num = parseFloat(str);
                  if (assetsStateValue == "报废") {
                       mygrid.setRowColor(i+1,"#049CFF");

                    // for(var a=1;a<=rownum;a++){
                           // mygrid.setRowTextStyle(i+1, "color:blue;"); 
                      // } 

