			//assetsStateCell .setValue(tempStr);
		}

		if (!isNaN(num) && num == 0 && assetsStateValue != "报废") {
			var tempStr = "<font color='red'>" + str + "</font>";
			orgiValueCell.setValue(tempStr);
		}
	}
}


function viewQrCode(){
   var ids="";
    mygrid.forEachRow(function(id){
        var isSelect=mygrid.cellById(id,0).getValue();
        if(isSelect==1){
            // alert(urlParamScriptArray[id][0]);
            ids=ids+urlParamScriptArray[id-1][0]+","; 
        }
    });

   if(ids.length>1503){
       new iMsgBox("选择的条数不能超过150条！", "系统提示","e").showAlert();
   }else if(ids){
      var url=basePath+"asset/assetsMainInfo.action?method=displayOrCreateQR&ids="+ids;
       openDynWin3(url);
   }else{
       new iMsgBox("请选择要生成的条码!", "系统提示").showAlert();
    }
  }
